changeSet.sql:
ALTER TABLE healthcare_services OWNER TO ${liquibase_user};
GRANT ALL ON TABLE healthcare_services TO ${liquibase_user};
GRANT SELECT, INSERT, UPDATE ON TABLE healthcare_services TO ${server_users_group};
CREATE INDEX healthcare_services_healthcare_service_id_index ON healthcare_services USING btree (healthcare_service_id);
CREATE INDEX healthcare_services_healthcare_service_index ON healthcare_services USING gin (healthcare_service);
CREATE INDEX healthcare_services_healthcare_service_id_version_index ON healthcare_services USING btree (healthcare_service_id, version);
