changeSet.sql:
ALTER TABLE locations OWNER TO ${liquibase_user};
GRANT ALL ON TABLE locations TO ${liquibase_user};
GRANT SELECT, INSERT, UPDATE ON TABLE locations TO ${server_users_group};
CREATE INDEX locations_location_id_index ON locations USING btree (location_id);
CREATE INDEX locations_location_index ON locations USING gin (location);
CREATE INDEX locations_location_id_version_index ON locations USING btree (location_id, version);
