changeSet.sql:
ALTER TABLE organizations OWNER TO ${liquibase_user};
GRANT ALL ON TABLE organizations TO ${liquibase_user};
GRANT SELECT, INSERT, UPDATE ON TABLE organizations TO ${server_users_group};
CREATE INDEX organizations_organization_id_index ON organizations USING btree (organization_id);
CREATE INDEX organizations_organization_index ON patients USING gin (patient);
CREATE INDEX organizations_organization_id_version_index ON organizations USING btree (organization_id, version);
