changeSet.sql:
ALTER TABLE structure_definitions OWNER TO ${liquibase_user};
GRANT ALL ON TABLE structure_definitions TO ${liquibase_user};
GRANT SELECT, INSERT, UPDATE ON TABLE structure_definitions TO ${server_users_group};
CREATE INDEX structure_definitions_structure_definition_id_index ON structure_definitions USING btree (structure_definition_id);
CREATE INDEX structure_definitions_structure_definition_index ON structure_definitions USING gin (structure_definition);
CREATE INDEX structure_definitions_structure_definition_id_version_index ON structure_definitions USING btree (structure_definition_id, version);
