CREATE INDEX practitioner_roles_practitioner_role_index ON practitioner_roles USING gin (practitioner_role);
CREATE INDEX practitioner_roles_practitioner_role_id_version_index ON practitioner_roles USING btree (practitioner_role_id, version);
