changeSet.sql:
ALTER TABLE practitioners OWNER TO ${liquibase_user};
GRANT ALL ON TABLE practitioners TO ${liquibase_user};
GRANT SELECT, INSERT, UPDATE ON TABLE practitioners TO ${server_users_group};
CREATE INDEX practitioners_practitioner_id_index ON practitioners USING btree (practitioner_id);
CREATE INDEX practitioners_practitioner_index ON practitioners USING gin (practitioner);
CREATE INDEX practitioners_practitioner_id_version_index ON practitioners USING btree (practitioner_id, version);
