changeSet.sql:
ALTER TABLE provenances OWNER TO ${liquibase_user};
GRANT ALL ON TABLE provenances TO ${liquibase_user};
GRANT SELECT, INSERT, UPDATE ON TABLE provenances TO ${server_users_group};
CREATE INDEX provenances_provenance_id_index ON provenances USING btree (provenance_id);
CREATE INDEX provenances_provenance_index ON provenances USING gin (provenance);
CREATE INDEX provenances_provenance_id_version_index ON provenances USING btree (provenance_id, version);
